        if (!hasAdminPrivileges()) {
            throw new SecurityException();
        }

        if (filter == null || filter.isEmpty()) {
            return clients.values();
        }

        List<ClientTO> result = new ArrayList<>();
        String lowerFilter = filter.toLowerCase();

        for (ClientTO client : clients.values()) {
            if (client.firstName.toLowerCase().contains(lowerFilter) ||
                client.lastName.toLowerCase().contains(lowerFilter) ||
                client.email.toLowerCase().contains(lowerFilter)) {
                result.add(client);
            }
        }

        return result;
    }

    public Collection<ClientTO> findClientsByName(String name) {
        if (name == null || name.isEmpty()) {
            throw new IllegalArgumentException();
        }

        if (!hasAdminPrivileges()) {
            throw new SecurityException();
        }

