        String lowerName = name.toLowerCase();

        for (ClientTO client : clients.values()) {
            if (client.firstName.toLowerCase().contains(lowerName) ||
                client.lastName.toLowerCase().contains(lowerName)) {
                result.add(client);
            }
        }

        return result;
    }

    public boolean hasAdminPrivileges() {
        return false;
    }
}

