import java.nio.file.*;
import java.util.*;
import java.io.IOException;
import java.nio.charset.StandardCharsets;

class AdvancedFileProcessor {
    private Path directoryPath;

    public AdvancedFileProcessor(String directoryPath) {
        this.directoryPath = Paths.get(directoryPath);
        if (!Files.isDirectory(this.directoryPath)) {
            throw new IllegalArgumentException("Provided path is not a directory");
        }
    }

    private void processFileContent(Path file, Map<String, Integer> wordFrequency) throws IOException {
        String content = Files.readString(file, StandardCharsets.UTF_8);
        String[] words = content.split("\\s+");

        for (String word : words) {
            // Convert to lowercase and remove non-alphabetic characters
            String cleanedWord = word.toLowerCase().replaceAll("[^a-z]", "");

            // Ignore empty strings
            if (!cleanedWord.isEmpty()) {
                wordFrequency.put(cleanedWord, wordFrequency.getOrDefault(cleanedWord, 0) + 1);
            }
        }
    }

    public Map<String, Object> processFiles() {
        Map<String, Object> result = new HashMap<>();
        int fileCount = 0;
        long totalSize = 0L;
        String largestFile = "N/A";
        long largestFileSize = -1L;
        Map<String, Integer> wordFrequency = new HashMap<>();

        try {
            for (Path path : Files.newDirectoryStream(directoryPath)) {
                if (Files.isRegularFile(path)) {
                    fileCount++;
                    long fileSize = Files.size(path);
                    totalSize += fileSize;

                    if (fileSize > largestFileSize) {
                        largestFileSize = fileSize;
                        largestFile = path.getFileName().toString();
                    }

                    // Process file content for word frequency
                    processFileContent(path, wordFrequency);
                }
            }
        } catch (IOException e) {
            // Handle exception if needed, though for this task we'll just return zeros
        }

        result.put("fileCount", fileCount);
        result.put("totalSize", totalSize);
        result.put("largestFile", largestFile);
        result.put("wordFrequency", wordFrequency);
        return result;
    }
}

