import java.nio.file.*;
import java.util.*;
import java.io.IOException;

class TestCases {

    @TempDir
    Path tempDir;

    @Test
    public void testProcessFileContent() throws IOException {
        // Create a test file with known content
        Path testFile = tempDir.resolve("test.txt");
        String content = "Hello, World! This is a test. Hello world!";
        Files.writeString(testFile, content);

        // Create an instance of AdvancedFileProcessor
        AdvancedFileProcessor processor = new AdvancedFileProcessor(tempDir.toString());

        // Create a map to hold word frequencies
        Map<String, Integer> wordFrequency = new HashMap<>();

        // Use reflection to access the private method
        try {
            java.lang.reflect.Method method = AdvancedFileProcessor.class.getDeclaredMethod(
                "processFileContent", Path.class, Map.class);
            method.setAccessible(true);
            method.invoke(processor, testFile, wordFrequency);

            // Verify the word frequencies
            assertEquals(2, wordFrequency.get("hello"));
