        // Create test files
        Path file1 = tempDir.resolve("file1.txt");
        Path file2 = tempDir.resolve("file2.txt");

        Files.writeString(file1, "the quick brown fox jumps over the lazy dog");
        Files.writeString(file2, "the quick brown fox");

        AdvancedFileProcessor processor = new AdvancedFileProcessor(tempDir.toString());
        Map<String, Object> result = processor.processFiles();

        @SuppressWarnings("unchecked")
        Map<String, Integer> wordFrequency = (Map<String, Integer>) result.get("wordFrequency");

        // Convert to list to check order
        List<Map.Entry<String, Integer>> entries = new ArrayList<>(wordFrequency.entrySet());

        // Verify it's sorted by value in descending order
        for (int i = 0; i < entries.size() - 1; i++) {
            assertTrue(entries.get(i).getValue() >= entries.get(i + 1).getValue(), 
                "Word frequency map should be sorted by value in descending order");
        }

        // Verify specific values
        assertEquals(3, wordFrequency.get("the"));
        assertEquals(2, wordFrequency.get("quick"));
        assertEquals(2, wordFrequency.get("brown"));
        assertEquals(2, wordFrequency.get("fox"));
    }

    @Test
    public void testSortByValueMethodDirectly() {
        AdvancedFileProcessor processor = new AdvancedFileProcessor(tempDir.toString());

        // Use reflection to access private method
        try {
            java.lang.reflect.Method method = AdvancedFileProcessor.class.getDeclaredMethod(
                "sortByValue", Map.class);
            method.setAccessible(true);

            Map<String, Integer> input = new HashMap<>();
            input.put("a", 1);
            input.put("b", 3);
            input.put("c", 2);

            Map<String, Integer> result = (Map<String, Integer>) method.invoke(processor, input);

            // Check that it's a LinkedHashMap
            assertTrue(result instanceof LinkedHashMap, "Result should be a LinkedHashMap");

            // Check order
            List<String> keys = new ArrayList<>(result.keySet());
            assertEquals("b", keys.get(0));
            assertEquals("c", keys.get(1));
            assertEquals("a", keys.get(2));

        } catch (Exception e) {
            fail("Failed to test sortByValue method: " + e.getMessage());
        }
    }
}

