    }

    public void setProfileURL(String profileURL) {
        this.profileURL = profileURL;
    }
}

class UserManager {
    private Map<String, User> users;
    private static final Pattern STEAM_URL_PATTERN = Pattern.compile("^(https?://)?(www\\.)?steamcommunity\\.com/.*$");

    public UserManager() {
        this.users = new HashMap<>();
    }

    public void addUser(String steam32Id, String username, String profileURL, String avatarMedium, String avatarFull) {
        if (steam32Id == null || steam32Id.length() != 17) {
            throw new IllegalArgumentException("Invalid Steam ID format");
        }
        if (users.containsKey(steam32Id)) {
            throw new IllegalArgumentException("User with this Steam ID already exists");
        }
        if (username == null || username.length() < 2 || username.length() > 32) {
            throw new IllegalArgumentException("Username must be 2-32 characters");
        }
        if (profileURL == null || !STEAM_URL_PATTERN.matcher(profileURL).matches()) {
            throw new IllegalArgumentException("Invalid Steam profile URL");
        }
        if (avatarMedium == null || avatarMedium.isEmpty()) {
            throw new IllegalArgumentException("Avatar medium cannot be null or empty");
        }
        if (avatarFull == null || avatarFull.isEmpty()) {
            throw new IllegalArgumentException("Avatar full cannot be null or empty");
        }
        User user = new User(steam32Id, username, profileURL, avatarMedium, avatarFull);
        users.put(steam32Id, user);
    }

    public User getUserBySteamId(String steam32Id) {
        return users.get(steam32Id);
    }

    public void updateUser(String steam32Id, String newUsername, String newProfileURL) {
        User user = users.get(steam32Id);
        if (user == null) {
            throw new IllegalArgumentException("User not found");
        }
        if (newUsername == null || newUsername.length() < 2 || newUsername.length() > 32) {
            throw new IllegalArgumentException("Username must be 2-32 characters");
        }
        if (newProfileURL == null || !STEAM_URL_PATTERN.matcher(newProfileURL).matches()) {
            throw new IllegalArgumentException("Invalid Steam profile URL");
        }
        user.setUsername(newUsername);
        user.setProfileURL(newProfileURL);
    }
}

