    private Node<D> tail;
    private int size;

    private static class Node<D> {
        D data;
        Node<D> next;
        Node<D> prev;

        Node(D data) {
            this.data = data;
        }
    }

    public LinkedList() {
        this.head = null;
        this.tail = null;
        this.size = 0;
    }

    public void add(D data) {
        Node<D> newNode = new Node<>(data);
        if (head == null) {
            head = newNode;
            tail = newNode;
        } else {
            tail.next = newNode;
            newNode.prev = tail;
            tail = newNode;
        }
        size++;
    }

    public void add(int index, D data) {
        if (index < 0 || index > size) {
            throw new IndexOutOfBoundsException("Index: " + index + ", Size: " + size);
        }

        if (index == size) {
            // Adding at the end
            add(data);
            return;
        }

        Node<D> newNode = new Node<>(data);
        if (index == 0) {
            // Adding at the beginning
            if (head != null) {
                newNode.next = head;
                head.prev = newNode;
            } else {
                // List is empty
                tail = newNode;
            }
            head = newNode;
        } else {
            // Adding in the middle
            Node<D> current = getNode(index);
            Node<D> previous = current.prev;

            newNode.next = current;
            newNode.prev = previous;
            previous.next = newNode;
            current.prev = newNode;
        }
        size++;
    }

    public int size() {
        return size;
    }

    public D get(int index) {
        Node<D> node = getNode(index);
        return node.data;
    }

    private Node<D> getNode(int index) {
        if (index < 0 || index >= size) {
            throw new IndexOutOfBoundsException("Index: " + index + ", Size: " + size);
        }

        Node<D> current;
        if (index < size / 2) {
            // Traverse from head
            current = head;
            for (int i = 0; i < index; i++) {
                current = current.next;
            }
        } else {
            // Traverse from tail
            current = tail;
            for (int i = size - 1; i > index; i--) {
                current = current.prev;
            }
        }
        return current;
    }

    public D remove() {
        if (tail == null) {
            throw new NoSuchElementException();
        }

        D data = tail.data;

        if (head == tail) { // Only one element
            head = null;
            tail = null;
        } else {
            tail = tail.prev;
            tail.next = null;
        }

        size--;
        return data;
    }

    public D remove(int index) {
        Node<D> nodeToRemove = getNode(index);

        if (nodeToRemove.prev != null) {
            nodeToRemove.prev.next = nodeToRemove.next;
        } else {
            head = nodeToRemove.next;
        }

        if (nodeToRemove.next != null) {
            nodeToRemove.next.prev = nodeToRemove.prev;
        } else {
            tail = nodeToRemove.prev;
        }

        size--;
        return nodeToRemove.data;
    }

    public boolean isEmpty() {
        return size == 0;
    }

    public void clear() {
        head = null;
        tail = null;
        size = 0;
    }

    public boolean contains(D data) {
        Node<D> current = head;
        while (current != null) {
            if (current.data == null ? data == null : current.data.equals(data)) {
                return true;
            }
            current = current.next;
        }
        return false;
    }

    public String toString() {
        StringBuilder sb = new StringBuilder();
        sb.append("[");
        Node<D> current = head;
        while (current != null) {
            sb.append(current.data);
            if (current.next != null) {
                sb.append(", ");
            }
            current = current.next;
        }
        sb.append("]");
        return sb.toString();
    }
}

