    void testToString() {
        LinkedList<Integer> list = new LinkedList<>();

        // Empty list
        assertEquals("[]", list.toString());

        // Single element
        list.add(42);
        assertEquals("[42]", list.toString());

        // Multiple elements
        list.add(10);
        list.add(20);
        assertEquals("[42, 10, 20]", list.toString());
    }
}

