import java.util.*;

public class FinancialReportAnalyzer {
    public static class PolicyData {
        private String previousRiskCode;
        private String currentRiskCode;
        private String previousRiskName;
        private String currentRiskName;
        private double premium;

        // Constructor
        public PolicyData(String previousRiskCode, String currentRiskCode, 
                         String previousRiskName, String currentRiskName, double premium) {
            this.previousRiskCode = previousRiskCode;
            this.currentRiskCode = currentRiskCode;
            this.previousRiskName = previousRiskName;
            this.currentRiskName = currentRiskName;
            this.premium = premium;
        }

        // Getters
        public String getPreviousRiskCode() { return previousRiskCode; }
        public String getCurrentRiskCode() { return currentRiskCode; }
        public String getPreviousRiskName() { return previousRiskName; }
        public String getCurrentRiskName() { return currentRiskName; }
        public double getPremium() { return premium; }
    }

    public Map<String, Object> analyzePolicies(List<PolicyData> policies) {
        Map<String, Object> result = new HashMap<>();

        double totalPremiums = 0.0;
        int policyCount = 0;

        if (policies != null) {
            for (PolicyData policy : policies) {
                totalPremiums += policy.getPremium();
                policyCount++;
            }
        }

        result.put("totalPremiums", totalPremiums);
        result.put("policyCount", policyCount);

        return result;
    }
}

