import java.util.*;
import java.util.concurrent.*;

public class UserSettingsManager {
    private final Map<String, Map<String, Object>> userSettings = new ConcurrentHashMap<>();

    public void saveUserSetting(String key, Object value, String username) {
        if (!(value instanceof String || value instanceof Integer || value instanceof Boolean)) {
            throw new IllegalArgumentException("Unsupported value type: " + value.getClass().getSimpleName());
        }

        userSettings.computeIfAbsent(username, k -> new ConcurrentHashMap<>()).put(key, value);
    }

    public <T> T getUserSetting(String key, String username, Class<T> type) {
        Map<String, Object> userMap = userSettings.get(username);
        if (userMap == null) {
            return null;
        }

        Object value = userMap.get(key);
        if (value == null) {
            return null;
        }

        if (!type.isInstance(value)) {
            throw new ClassCastException("Cannot cast " + value.getClass().getName() + " to " + type.getName());
        }

        return type.cast(value);
    }

    public void updateUserSettings(Map<String, Object> settings, String username) {
        Map<String, Object> validSettings = new HashMap<>();
        for (Map.Entry<String, Object> entry : settings.entrySet()) {
            Object value = entry.getValue();
            if (value instanceof String || value instanceof Integer || value instanceof Boolean) {
                validSettings.put(entry.getKey(), value);
            }
        }
        userSettings.computeIfAbsent(username, k -> new ConcurrentHashMap<>()).putAll(validSettings);
    }

    // Getter method for testing purposes
    public Map<String, Map<String, Object>> getUserSettings() {
        return userSettings;
    }
}

