import java.sql.Timestamp;
import java.util.ArrayList;
import java.util.List;

class BudgetVersionManager {
    public List<String> validateBudgetVersion(VersaoOrcamento version) {
        List<String> errors = new ArrayList<>();
        if (version == null) {
            errors.add("Budget version cannot be null");
        } else {
            if (version.getIdVersaoOrcamento() == null) {
                errors.add("Version ID cannot be null");
            }

            // Validate description message
            if (version.getDescricaoMensagem() == null || version.getDescricaoMensagem().trim().isEmpty()) {
                errors.add("Description message is required");
            }

            // Validate status
            String status = version.getIdtStatus();
            if (status == null || !(status.equals("A") || status.equals("R") || status.equals("P"))) {
                errors.add("Status must be one of: A, R, P");
            }
        }
        return errors;
    }
}

class VersaoOrcamento {
    private VersaoOrcamentoId idVersaoOrcamento;
    private String descricaoMensagem;
    private String idtStatus;

    public VersaoOrcamentoId getIdVersaoOrcamento() {
        return idVersaoOrcamento;
    }

    public void setIdVersaoOrcamento(VersaoOrcamentoId idVersaoOrcamento) {
        this.idVersaoOrcamento = idVersaoOrcamento;
    }

    public String getDescricaoMensagem() {
        return descricaoMensagem;
    }

    public void setDescricaoMensagem(String descricaoMensagem) {
        this.descricaoMensagem = descricaoMensagem;
    }

    public String getIdtStatus() {
        return idtStatus;
    }

    public void setIdtStatus(String idtStatus) {
        this.idtStatus = idtStatus;
    }
}

