import java.sql.Timestamp;
import java.util.ArrayList;
import java.util.List;

class BudgetVersionManager {
    public List<String> validateBudgetVersion(VersaoOrcamento version) {
        List<String> errors = new ArrayList<>();
        if (version == null) {
            errors.add("Budget version cannot be null");
        } else {
            if (version.getIdVersaoOrcamento() == null) {
                errors.add("Version ID cannot be null");
            }

            // Validate description message
            if (version.getDescricaoMensagem() == null || version.getDescricaoMensagem().trim().isEmpty()) {
                errors.add("Description message is required");
            }

            // Validate status
            String status = version.getIdtStatus();
            if (status == null || !(status.equals("A") || status.equals("R") || status.equals("P"))) {
                errors.add("Status must be one of: A, R, P");
            }

            // Validate effective date
            if (version.getDataVigencia() == null) {
                errors.add("Effective date is required");
            }
        }
        return errors;
    }

    public List<VersaoOrcamento> filterVersions(List<VersaoOrcamento> versions, String status, Timestamp fromDate, Timestamp toDate) {
        List<VersaoOrcamento> result = new ArrayList<>();

        if (versions == null) {
            return result;
        }

        for (VersaoOrcamento version : versions) {
            if (version == null) {
                continue;
            }

            // Filter by status if provided
            if (status != null && !status.equals(version.getIdtStatus())) {
                continue;
            }

            // Filter by date range if provided
            Timestamp versionDate = version.getDataVigencia();
            if (versionDate == null) {
                continue;
            }

            if (fromDate != null && versionDate.before(fromDate)) {
                continue;
            }

            if (toDate != null && versionDate.after(toDate)) {
                continue;
            }

            result.add(version);
        }

        return result;
    }

    public BudgetVersionAnalysis analyzeVersions(List<VersaoOrcamento> versions) {
        BudgetVersionAnalysis analysis = new BudgetVersionAnalysis();

        if (versions == null || versions.isEmpty()) {
            return analysis;
        }

        int total = 0;
        int approved = 0;
        int rejected = 0;
        int pending = 0;
        Timestamp earliest = null;
        Timestamp latest = null;

        for (VersaoOrcamento version : versions) {
            if (version == null) {
                continue;
            }

            total++;

            String status = version.getIdtStatus();
            if ("A".equals(status)) {
                approved++;
            } else if ("R".equals(status)) {
                rejected++;
            } else if ("P".equals(status)) {
                pending++;
            }

            Timestamp date = version.getDataVigencia();
            if (date != null) {
                if (earliest == null || date.before(earliest)) {
                    earliest = date;
                }
                if (latest == null || date.after(latest)) {
                    latest = date;
                }
            }
        }

        analysis.setTotal(total);
        analysis.setApproved(approved);
        analysis.setRejected(rejected);
        analysis.setPending(pending);
        analysis.setEarliest(earliest);
        analysis.setLatest(latest);

        return analysis;
    }
}

