    private Map<Integer, Product> productDatabase;
    private LoggerService logger;
    private Set<Integer> restrictedCategories;

    public EnhancedProductManager(LoggerService logger, List<Integer> restrictedCategories) {
        this.productDatabase = new HashMap<>();
        this.logger = logger;
        this.restrictedCategories = new HashSet<>(restrictedCategories);
    }

    public Map<String, String> addProduct(Product product) {
        Map<String, String> result = new HashMap<>();
        if (product == null) {
            result.put("status", "error");
            result.put("message", "Product cannot be null");
            return result;
        }
        if (product.getName() == null || product.getName().trim().isEmpty()) {
            result.put("status", "error");
            result.put("message", "Product name cannot be empty");
            return result;
        }
        if (restrictedCategories.contains(product.getCategoryId())) {
            result.put("status", "error");
            result.put("message", "Products in category " + product.getCategoryId() + " are not allowed");
            return result;
        }
        if (productDatabase.containsKey(product.getId())) {
            result.put("status", "error");
            result.put("message", "Product with ID " + product.getId() + " already exists");
            return result;
        }
        productDatabase.put(product.getId(), product);
        logger.log("Product added: " + product.getName());
        result.put("status", "success");
        result.put("message", "Product added successfully");
        return result;
    }

    public List<Product> getAllProducts() {
        return new ArrayList<>(productDatabase.values());
    }

    public Map<String, String> removeProduct(int productId) {
        Map<String, String> result = new HashMap<>();
        if (!productDatabase.containsKey(productId)) {
            result.put("status", "error");
            result.put("message", "Product with ID " + productId + " does not exist");
            return result;
        }
        Product removedProduct = productDatabase.remove(productId);
        logger.log("Product removed: " + removedProduct.getName());
        result.put("status", "success");
        result.put("message", "Product removed successfully");
        return result;
    }
}

