class MatrixProcessor {
    public MatrixResult processMatrix(int[][] matrix) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            return new MatrixResult(matrix);
        }

        int rows = matrix.length;
        int cols = matrix[0].length;
        boolean[] zeroRows = new boolean[rows];
        boolean[] zeroCols = new boolean[cols];

        // First pass: identify which rows and columns contain zeros
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (matrix[i][j] == 0) {
                    zeroRows[i] = true;
                    zeroCols[j] = true;
                }
            }
        }

        // Second pass: zero out identified rows and columns
        int[][] result = new int[rows][cols];
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (zeroRows[i] || zeroCols[j]) {
                    result[i][j] = 0;
                } else {
                    result[i][j] = matrix[i][j];
                }
            }
        }

        return new MatrixResult(result);
    }

    static class MatrixResult {
        final int[][] processedMatrix;

        public MatrixResult(int[][] processedMatrix) {
            this.processedMatrix = processedMatrix;
        }
    }
}

