class MatrixProcessor {
    public MatrixResult processMatrix(int[][] matrix) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            throw new IllegalArgumentException("Matrix cannot be null or empty");
        }

        long startTime = System.currentTimeMillis();

        int rows = matrix.length;
        int cols = matrix[0].length;
        boolean[] zeroRows = new boolean[rows];
        boolean[] zeroCols = new boolean[cols];
        int zeroCount = 0;

        // First pass: identify which rows and columns contain zeros and count zeros
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (matrix[i][j] == 0) {
                    zeroRows[i] = true;
                    zeroCols[j] = true;
                    zeroCount++;
                }
            }
        }

        // Second pass: zero out identified rows and columns
        int[][] result = new int[rows][cols];
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (zeroRows[i] || zeroCols[j]) {
                    result[i][j] = 0;
                } else {
                    result[i][j] = matrix[i][j];
                }
            }
        }

        long endTime = System.currentTimeMillis();
        long processingTime = endTime - startTime;

        // Create a deep copy of the original matrix
        int[][] originalMatrixCopy = deepCopy(matrix);

        return new MatrixResult(result, originalMatrixCopy, zeroCount, processingTime);
    }

    // Helper method to create a deep copy of a matrix
    private int[][] deepCopy(int[][] original) {
        if (original == null) {
            return null;
        }

        int[][] copy = new int[original.length][];
        for (int i = 0; i < original.length; i++) {
            copy[i] = new int[original[i].length];
            System.arraycopy(original[i], 0, copy[i], 0, original[i].length);
        }
        return copy;
    }

    static class MatrixResult {
        final int[][] processedMatrix;
        final int[][] originalMatrix;
        final int zeroCount;
        final long processingTime;

        public MatrixResult(int[][] processedMatrix, int[][] originalMatrix, int zeroCount, long processingTime) {
            this.processedMatrix = processedMatrix;
            this.originalMatrix = originalMatrix;
            this.zeroCount = zeroCount;
            this.processingTime = processingTime;
        }
    }
}

