class MatrixProcessor {
    public MatrixResult processMatrix(int[][] matrix, boolean useSpaceEfficient) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            throw new IllegalArgumentException("Matrix cannot be null or empty");
        }

        long startTime = System.currentTimeMillis();

        int[][] result;
        String methodUsed;

        if (useSpaceEfficient) {
            result = zeroMatrixSpaceEfficient(matrix);
            methodUsed = "Space-Efficient";
        } else {
            result = zeroMatrixWithSets(matrix);
            methodUsed = "HashSet-Based";
        }

        int zeroCount = countZeros(result); // Count zeros in processed matrix

        long endTime = System.currentTimeMillis();
        long processingTime = endTime - startTime;

        // Create a deep copy of the original matrix
        int[][] originalMatrixCopy = deepCopy(matrix);

        return new MatrixResult(result, originalMatrixCopy, zeroCount, processingTime, methodUsed);
    }

    private int[][] zeroMatrixWithSets(int[][] matrix) {
        int rows = matrix.length;
        int cols = matrix[0].length;
        boolean[] zeroRows = new boolean[rows];
        boolean[] zeroCols = new boolean[cols];

        // First pass: identify which rows and columns contain zeros
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (matrix[i][j] == 0) {
                    zeroRows[i] = true;
                    zeroCols[j] = true;
                }
            }
        }

        // Second pass: zero out identified rows and columns
        int[][] result = deepCopy(matrix);
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (zeroRows[i] || zeroCols[j]) {
                    result[i][j] = 0;
                }
            }
        }

        return result;
    }

    private int[][] zeroMatrixSpaceEfficient(int[][] matrix) {
        int[][] workingMatrix = deepCopy(matrix);
        int rows = workingMatrix.length;
        int cols = workingMatrix[0].length;
        boolean firstRowHasZero = false;
        boolean firstColHasZero = false;

        // Check if first row has zero
        for (int j = 0; j < cols; j++) {
            if (workingMatrix[0][j] == 0) {
                firstRowHasZero = true;
                break;
            }
        }

        // Check if first column has zero
        for (int i = 0; i < rows; i++) {
            if (workingMatrix[i][0] == 0) {
                firstColHasZero = true;
                break;
            }
        }

        // Use first row and column as markers
        for (int i = 1; i < rows; i++) {
            for (int j = 1; j < cols; j++) {
                if (workingMatrix[i][j] == 0) {
                    workingMatrix[i][0] = 0;
                    workingMatrix[0][j] = 0;
                }
            }
        }

        // Zero out cells based on markers
        for (int i = 1; i < rows; i++) {
            for (int j = 1; j < cols; j++) {
                if (workingMatrix[i][0] == 0 || workingMatrix[0][j] == 0) {
                    workingMatrix[i][j] = 0;
                }
            }
        }

        // Zero out first row if needed
        if (firstRowHasZero) {
            for (int j = 0; j < cols; j++) {
                workingMatrix[0][j] = 0;
            }
        }

        // Zero out first column if needed
        if (firstColHasZero) {
            for (int i = 0; i < rows; i++) {
                workingMatrix[i][0] = 0;
            }
        }

        return workingMatrix;
    }

    // Helper method to count zeros in a matrix
    private int countZeros(int[][] matrix) {
        if (matrix == null || matrix.length == 0) {
            return 0;
        }

        int count = 0;
        for (int i = 0; i < matrix.length; i++) {
            if (matrix[i] != null) {
                for (int j = 0; j < matrix[i].length; j++) {
                    if (matrix[i][j] == 0) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    // Helper method to create a deep copy of a matrix
    private int[][] deepCopy(int[][] original) {
        if (original == null) {
            return null;
        }

        int[][] copy = new int[original.length][];
        for (int i = 0; i < original.length; i++) {
            if (original[i] != null) {
                copy[i] = new int[original[i].length];
                System.arraycopy(original[i], 0, copy[i], 0, original[i].length);
            } else {
                copy[i] = null;
            }
        }
        return copy;
    }

    static class MatrixResult {
        final int[][] processedMatrix;
        final int[][] originalMatrix;
        final int zeroCount;
        final long processingTime;
        final String methodUsed;

        public MatrixResult(int[][] processedMatrix, int[][] originalMatrix, int zeroCount, long processingTime, String methodUsed) {
            this.processedMatrix = processedMatrix;
            this.originalMatrix = originalMatrix;
            this.zeroCount = zeroCount;
            this.processingTime = processingTime;
            this.methodUsed = methodUsed;
        }
    }
}

