    }

    @Test
    public void testThreadIdGeneration() throws InterruptedException {
        UserManager userManager = new UserManager();

        // Create users in multiple threads
        Thread[] threads = new Thread[10];
        UserManager.User[] users = new UserManager.User[10];

        for (int i = 0; i < 10; i++) {
            final int index = i;
            threads[i] = new Thread(() -> {
                users[index] = userManager.createUser("user" + index, "user" + index + "@example.com", "password");
            });
            threads[i].start();
        }

        // Wait for all threads to complete
        for (Thread thread : threads) {
            thread.join();
        }

        // Verify all IDs are unique and sequential
        Set<Long> ids = new HashSet<>();
        for (UserManager.User user : users) {
            assertNotNull(user);
            assertTrue(ids.add(user.getId()), "Duplicate ID found: " + user.getId());
        }

        assertEquals(10, ids.size());
    }
}

