import java.util.*;
import java.util.concurrent.atomic.AtomicLong;
import java.util.Optional;
import java.util.stream.Collectors;

public class UserManager {
    private List<User> users;
    private AtomicLong nextId;

    public UserManager() {
        users = new ArrayList<>();
        nextId = new AtomicLong(1L);
    }

    public User createUser(String username, String email, String password) {
        User user = new User(nextId.getAndIncrement(), username, email, password);
        users.add(user);
        return user;
    }

    public Optional<User> findUserById(Long id) {
        return users.stream()
                .filter(user -> user.getId().equals(id))
                .findFirst();
    }

    public List<User> getAllActiveUsers() {
        return users.stream()
                .filter(User::isActive)
                .collect(Collectors.toList());
    }

    public boolean deactivateUser(Long id) {
        Optional<User> userOptional = findUserById(id);
        if (userOptional.isPresent()) {
            userOptional.get().setActive(false);
            return true;
        }
        return false;
    }

    public User updateUser(Long id, String username, String email, String password) {
        Optional<User> userOptional = findUserById(id);
        if (!userOptional.isPresent()) {
            throw new IllegalArgumentException();
        }

        User user = userOptional.get();

        // Check if new username is already taken by another user
        if (username != null && !username.equals(user.getUsername())) {
            boolean usernameExists = users.stream()
                    .anyMatch(u -> u.getId() != id && u.getUsername().equals(username));
            if (usernameExists) {
                throw new IllegalArgumentException();
            }
            user.setUsername(username);
        }

        // Check if new email is already taken by another user
        if (email != null && !email.equals(user.getEmail())) {
            boolean emailExists = users.stream()
                    .anyMatch(u -> u.getId() != id && u.getEmail().equals(email));
            if (emailExists) {
                throw new IllegalArgumentException();
            }
            user.setEmail(email);
        }

        // Update password if not null
        if (password != null) {
            user.setPassword(password);
        }

        return user;
    }

    public static class User {
        private Long id;
        private String username;
        private String email;
        private String password;
        private boolean active;

        public User(Long id, String username, String email, String password) {
            this.id = id;
            this.username = username;
            this.email = email;
            this.password = password;
            this.active = true;
        }

        // Getters
        public Long getId() { return id; }
        public String getUsername() { return username; }
        public String getEmail() { return email; }
        public String getPassword() { return password; }
        public boolean isActive() { return active; }

        // Setters
        public void setUsername(String username) { this.username = username; }
        public void setEmail(String email) { this.email = email; }
        public void setPassword(String password) { this.password = password; }
        public void setActive(boolean active) { this.active = active; }
    }
}

