import java.util.*;
import java.time.format.DateTimeFormatter;
import java.time.LocalDateTime;
import java.util.UUID;

class TaskDatabaseManager {
    private List<Map<String, Object>> taskCache;
    private int nextId;

    public TaskDatabaseManager() {
        this.taskCache = new ArrayList<>();
        this.nextId = 1;
    }

    public Map<String, Object> insertTask(int taskType, String taskName, String taskContent, int studentId, String studentName) {
        Map<String, Object> task = new HashMap<>();
        task.put("taskId", UUID.randomUUID().toString());
        task.put("taskType", taskType);
        task.put("taskName", taskName);
        task.put("taskContent", taskContent);
        task.put("studentId", studentId);
        task.put("studentName", studentName);
        task.put("successCount", 0);
        task.put("failCount", 0);
        task.put("score", 0);
        task.put("status", "pending");
        task.put("creationTimestamp", LocalDateTime.now().format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")));

        taskCache.add(task);

        Map<String, Object> result = new HashMap<>();
        result.put("status", "success");
        result.put("taskId", task.get("taskId"));
        result.put("affectedRows", 1);

        nextId++;
        return result;
    }

    public Map<String, Object> queryTasks(Map<String, Object> criteria, int page, int pageSize) {
        // Filter tasks based on exact match for all specified criteria
        List<Map<String, Object>> filteredTasks = new ArrayList<>();

        for (Map<String, Object> task : taskCache) {
            boolean matchesAll = true;
            for (Map.Entry<String, Object> entry : criteria.entrySet()) {
                String key = entry.getKey();
                Object value = entry.getValue();

                if (!task.containsKey(key) || !task.get(key).equals(value)) {
                    matchesAll = false;
                    break;
                }
            }

            if (matchesAll) {
                filteredTasks.add(task);
            }
        }

        // Handle pagination with 1-based page numbering
        int totalItems = filteredTasks.size();
        int totalPages = (int) Math.ceil((double) totalItems / pageSize);

        // Clamp invalid page numbers to nearest valid page
        if (page < 1) page = 1;
        if (page > totalPages && totalPages > 0) page = totalPages;
        if (totalPages == 0) page = 1;

        // Calculate pagination bounds
        int startIndex = (page - 1) * pageSize;
        int endIndex = Math.min(startIndex + pageSize, totalItems);

        // Get paginated results
        List<Map<String, Object>> paginatedTasks = new ArrayList<>();
        if (startIndex < totalItems) {
            paginatedTasks = filteredTasks.subList(startIndex, endIndex);
        }

        // Build result map
        Map<String, Object> result = new HashMap<>();
        result.put("tasks", paginatedTasks);
        result.put("currentPage", page);
        result.put("totalPages", totalPages);
        result.put("totalItems", totalItems);

        return result;
    }

    // Public getter method to access taskCache for testing
    public List<Map<String, Object>> getTaskCache() {
        return taskCache;
    }
}

