        result.put("currentPage", page);
        result.put("totalPages", totalPages);
        result.put("totalItems", totalItems);

        return result;
    }

    public Map<String, Object> updateTaskStats(String taskId, int successIncrement, int failIncrement, int scoreIncrement) {
        // Find the task with the specified taskId
        for (Map<String, Object> task : taskCache) {
            if (task.get("taskId").equals(taskId)) {
                // Update the statistics
                int currentSuccess = (int) task.get("successCount");
                int currentFail = (int) task.get("failCount");
                int currentScore = (int) task.get("score");

                task.put("successCount", currentSuccess + successIncrement);
                task.put("failCount", currentFail + failIncrement);
                task.put("score", currentScore + scoreIncrement);

                // Check if successCount becomes greater than 0 and update status
                List<String> modifiedFields = new ArrayList<>();
                modifiedFields.add("successCount");
                modifiedFields.add("failCount");
                modifiedFields.add("score");

                if (currentSuccess + successIncrement > 0 && !"completed".equals(task.get("status"))) {
                    task.put("status", "completed");
                    modifiedFields.add("status");
                }

                // Return success response with modified fields
                Map<String, Object> result = new HashMap<>();
                result.put("status", "success");
                result.put("modifiedFields", modifiedFields);
                return result;
            }
        }

        // Task not found
        Map<String, Object> result = new HashMap<>();
        result.put("status", "error");
        result.put("message", "Task not found with ID: " + taskId);
        return result;
    }

    // Public getter method to access taskCache for testing
    public List<Map<String, Object>> getTaskCache() {
        return taskCache;
    }
