import java.util.*;

class StringProcessor {

    public static Map<String, Object> processString(String input) {
        if (input == null) {
            throw new IllegalArgumentException("Input string cannot be null");
        }

        Map<String, Object> results = new LinkedHashMap<>();

        // Handle empty string case
        if (input.isEmpty()) {
            results.put("reversedString", "");
            results.put("wordsReversed", "");
            results.put("wordCount", 0);
            results.put("charCount", 0L);
            return results;
        }

        String reversed = new StringBuilder(input).reverse().toString();
        results.put("reversedString", reversed);

        String[] words = input.split(" ");
        StringBuilder reversedWordsBuilder = new StringBuilder();
        for (int i = 0; i < words.length; i++) {
            if (i > 0) reversedWordsBuilder.append(" ");
            reversedWordsBuilder.append(new StringBuilder(words[i]).reverse().toString());
        }
        results.put("wordsReversed", reversedWordsBuilder.toString());

        // Trim the input and split by one or more whitespace characters
        String[] wordArray = input.trim().split("\\s+");
        // Handle the case where the input is empty or only whitespace
        int wordCount = (input.trim().isEmpty()) ? 0 : wordArray.length;
        results.put("wordCount", wordCount);

        // Count non-space characters
        long charCount = 0;
        for (char c : input.toCharArray()) {
            if (c != ' ') {
                charCount++;
            }
        }
        results.put("charCount", charCount);

        return results;
    }
}

