import java.util.*;

public class FragmentManager {
    private Map<String, Object> fragments;
    private Object currentFragment;
    private String currentFragmentTag;

    public FragmentManager() {
        fragments = new HashMap<>();
        currentFragment = null;
        currentFragmentTag = null;
    }

    public void addFragment(String tag, Object fragment) {
        if (tag == null || fragment == null) {
            throw new IllegalArgumentException("Tag and fragment cannot be null");
        }
        fragments.put(tag, fragment);
    }

    public boolean switchFragment(String tag) {
        if (tag == null) {
            return false;
        }
        if (tag.equals(currentFragmentTag)) {
            return true;
        }
        if (fragments.containsKey(tag)) {
            currentFragment = fragments.get(tag);
            currentFragmentTag = tag;
            return true;
        }
        return false;
    }

    public Object getCurrentFragment() {
        return currentFragment;
    }

    public String getCurrentFragmentTag() {
        return currentFragmentTag;
    }
}

