import java.util.*;
import java.util.regex.Pattern;
import java.util.regex.Matcher;

public class DocumentationValidator {
    public static final Pattern INVALID_CHAR_PATTERN = Pattern.compile("\\\\");
    public static final Pattern PLACEHOLDER_PATTERN = Pattern.compile("\\$\\{[^}]*}");
    public static final List<String> ALLOWED_PLACEHOLDERS = Collections.unmodifiableList(Arrays.asList("method", "class", "param", "return"));

    public static void validateDocumentation(String doc, String qualifiedName) {
        if (doc == null) {
            return;
        }

        if (doc.trim().isEmpty()) {
            throw new IllegalArgumentException("Empty documentation string for: " + qualifiedName);
        }

        Matcher matcher = INVALID_CHAR_PATTERN.matcher(doc);
        if (matcher.find()) {
            throw new IllegalArgumentException("Documentation contains invalid characters: " + qualifiedName);
        }

        Matcher placeholderMatcher = PLACEHOLDER_PATTERN.matcher(doc);
        while (placeholderMatcher.find()) {
            String placeholder = placeholderMatcher.group();
            String placeholderName = placeholder.substring(2, placeholder.length() - 1); // Remove ${ and }
            if (!ALLOWED_PLACEHOLDERS.contains(placeholderName)) {
                throw new IllegalArgumentException("Invalid placeholder '" + placeholderName + "' in documentation for: " + qualifiedName);
            }
        }
    }
}

