        variables.add(Math.abs(a));
        variables.add(Math.abs(b));

        // Add implication edges: (-a  b) and (-b  a)
        addImplication(-a, b);
        addImplication(-b, a);
    }

    private void addImplication(int from, int to) {
        if (!graph.containsKey(from)) {
            graph.put(from, new ArrayList<>());
        }
        graph.get(from).add(to);

        // Add reverse edge for reverse graph
        if (!reverseGraph.containsKey(to)) {
            reverseGraph.put(to, new ArrayList<>());
        }
        reverseGraph.get(to).add(from);
    }

    public boolean isSatisfiable() {
        // First pass: DFS on reverse graph to compute finishing times
        time = 0;
        visited = new HashSet<>();
        finishingTimes = new HashMap<>();

        // Get all nodes from the reverse graph
        Set<Integer> allNodes = new HashSet<>();
        allNodes.addAll(reverseGraph.keySet());
        for (List<Integer> edges : reverseGraph.values()) {
            allNodes.addAll(edges);
        }

        // Perform DFS on reverse graph in arbitrary order
        for (Integer node : allNodes) {
            if (!visited.contains(node)) {
                dfsReverse(node);
            }
        }

        // Second pass: DFS on original graph in decreasing order of finishing times
        visited = new HashSet<>();
        leaders = new HashMap<>();

        // Sort nodes by finishing time in descending order
        List<Integer> nodesByFinishingTime = new ArrayList<>(allNodes);
        nodesByFinishingTime.sort((a, b) -> Integer.compare(finishingTimes.get(b), finishingTimes.get(a)));

        // Perform DFS on original graph
        for (Integer node : nodesByFinishingTime) {
            if (!visited.contains(node)) {
                currentLeader = node;
                dfsOriginal(node);
            }
        }

        return true;
    }

    private void dfsReverse(int node) {
        visited.add(node);

        if (reverseGraph.containsKey(node)) {
            for (int neighbor : reverseGraph.get(node)) {
                if (!visited.contains(neighbor)) {
                    dfsReverse(neighbor);
                }
            }
        }

        finishingTimes.put(node, time++);
    }

    private void dfsOriginal(int node) {
        visited.add(node);
        leaders.put(node, currentLeader);

        if (graph.containsKey(node)) {
            for (int neighbor : graph.get(node)) {
                if (!visited.contains(neighbor)) {
                    dfsOriginal(neighbor);
                }
            }
        }
    }

