    @org.junit.jupiter.api.Test
    public void testTanhActivation() {
        NeuralNetworkCalculator calculator = new NeuralNetworkCalculator(1, 1, 1, new double[]{0, 1}, new double[][]{{0, 1}}, new double[][]{{0, 1}});

        // Using reflection to access the private method
        try {
            java.lang.reflect.Method method = NeuralNetworkCalculator.class.getDeclaredMethod("tanhActivation", double.class);
            method.setAccessible(true);

            // Test tanh(0) = 0
            double result1 = (double) method.invoke(calculator, 0.0);
            org.junit.jupiter.api.Assertions.assertEquals(0.0, result1, 1e-10);

            // Test tanh(1)  0.7615941559557649
            double result2 = (double) method.invoke(calculator, 1.0);
            org.junit.jupiter.api.Assertions.assertEquals(0.7615941559557649, result2, 1e-10);

            // Test tanh(-1)  -0.7615941559557649
            double result3 = (double) method.invoke(calculator, -1.0);
            org.junit.jupiter.api.Assertions.assertEquals(-0.7615941559557649, result3, 1e-10);
        } catch (Exception e) {
            org.junit.jupiter.api.Assertions.fail("Failed to test tanhActivation method: " + e.getMessage());
        }
    }

    // Test hidden layer computation with tanh activation
    @org.junit.jupiter.api.Test
    public void testHiddenLayerWithTanh() {
        // Create a network with 2 inputs, 2 hidden neurons
        // Weights for hidden layer:
        // Neuron 1: bias=0, w1=1, w2=1
        // Neuron 2: bias=0, w1=-1, w2=-1
        double[] weights = {0, 1, 1, 0, -1, -1};
        double[][] inputNormParams = {{0, 1}, {0, 1}};
        double[][] outputNormParams = {{0, 1}};

        NeuralNetworkCalculator calculator = new NeuralNetworkCalculator(2, 2, 1, weights, inputNormParams, outputNormParams);

        // Input [0.5, 0.5] maps to [0, 0] in normalized space
        double[] input1 = {0.5, 0.5};
        double[] result1 = calculator.calculate(input1);

        // For normalized input [0, 0]:
        // Neuron 1: tanh(0*1 + 0*1 + 0) = tanh(0) = 0
        // Neuron 2: tanh(0*-1 + 0*-1 + 0) = tanh(0) = 0
        org.junit.jupiter.api.Assertions.assertEquals(2, result1.length);
        org.junit.jupiter.api.Assertions.assertEquals(0.0, result1[0], 1e-10);
        org.junit.jupiter.api.Assertions.assertEquals(0.0, result1[1], 1e-10);

        // Input [1, 1] maps to [1, 1] in normalized space
        double[] input2 = {1, 1};
        double[] result2 = calculator.calculate(input2);

        // For normalized input [1, 1]:
        // Neuron 1: tanh(1*1 + 1*1 + 0) = tanh(2)  0.964027580075817
        // Neuron 2: tanh(1*-1 + 1*-1 + 0) = tanh(-2)  -0.964027580075817
        org.junit.jupiter.api.Assertions.assertEquals(2, result2.length);
        org.junit.jupiter.api.Assertions.assertEquals(0.964027580075817, result2[0], 1e-10);
        org.junit.jupiter.api.Assertions.assertEquals(-0.964027580075817, result2[1], 1e-10);
    }
}

