class NeuralNetworkCalculator {
    private final int inputSize;
    private final int hiddenSize;
    private final int outputSize;
    private final double[] weights;
    private final double[][] inputNormalizationParams;
    private final double[][] outputNormalizationParams;

    public NeuralNetworkCalculator(int inputSize, int hiddenSize, int outputSize, 
                                 double[] weights, double[][] inputNormParams, 
                                 double[][] outputNormParams) {
        this.inputSize = inputSize;
        this.hiddenSize = hiddenSize;
        this.outputSize = outputSize;
        this.weights = weights;
        this.inputNormalizationParams = inputNormParams;
        this.outputNormalizationParams = outputNormParams;
    }

    public double[] calculate(double[] input) {
        if (input.length != this.inputSize) {
            throw new IllegalArgumentException("Input array length (" + input.length + ") does not match expected input size (" + this.inputSize + ")");
        }

        double[] normalizedInput = normalizeInput(input);

        // Compute hidden layer outputs
        double[] hiddenOutputs = new double[hiddenSize];

        // Process each hidden neuron
        for (int h = 0; h < hiddenSize; h++) {
            // Calculate index in weights array for this neuron's parameters
            // Each neuron has (inputSize + 1) weights: 1 bias + inputSize weights
            int weightIndexStart = h * (inputSize + 1);

            // Get bias (first weight for this neuron)
            double bias = weights[weightIndexStart];

            // Calculate weighted sum
            double weightedSum = bias;
            for (int i = 0; i < inputSize; i++) {
                double weight = weights[weightIndexStart + 1 + i];
                weightedSum += weight * normalizedInput[i];
            }

            // Apply tanh activation
            hiddenOutputs[h] = tanhActivation(weightedSum);
        }

        // Compute output layer with linear activation
        double[] output = new double[outputSize];
        int outputWeightsStartIndex = hiddenSize * (inputSize + 1);

        for (int o = 0; o < outputSize; o++) {
            int weightIndexStart = outputWeightsStartIndex + o * (hiddenSize + 1);

            // Get bias (first weight for this output neuron)
            double bias = weights[weightIndexStart];

            // Calculate weighted sum
            double weightedSum = bias;
            for (int h = 0; h < hiddenSize; h++) {
                double weight = weights[weightIndexStart + 1 + h];
                weightedSum += weight * hiddenOutputs[h];
            }

            // Linear activation (no activation function)
            output[o] = weightedSum;
        }

        // Denormalize output before returning
        return denormalizeOutput(output);
    }

    private double[] normalizeInput(double[] input) {
        double[] normalized = new double[input.length];
        for (int i = 0; i < input.length; i++) {
            double min = inputNormalizationParams[i][0];
            double max = inputNormalizationParams[i][1];

            // Handle case where min equals max to avoid division by zero
            if (max == min) {
                normalized[i] = 0; // or could be any constant in [-1, 1]
            } else {
                // Scale to [0, 1] first
                double scaled = (input[i] - min) / (max - min);
                // Scale to [-1, 1]
                normalized[i] = 2 * scaled - 1;
            }
        }
        return normalized;
    }

    private double[] denormalizeOutput(double[] normalizedOutput) {
        double[] denormalized = new double[normalizedOutput.length];
        for (int i = 0; i < normalizedOutput.length; i++) {
            double min = outputNormalizationParams[i][0];
            double max = outputNormalizationParams[i][1];

            // Convert from [-1, 1] to [0, 1]
            double scaled = (normalizedOutput[i] + 1) / 2;

            // Convert from [0, 1] to [min, max]
            denormalized[i] = scaled * (max - min) + min;
        }
        return denormalized;
    }

    private double tanhActivation(double x) {
        return Math.tanh(x);
    }
}

