import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

class TestCases {
    @Test
    public void testOutputLayerComputationWithLinearActivation() {
        // Setup a simple network: 2 inputs, 2 hidden neurons, 1 output
        int inputSize = 2;
        int hiddenSize = 2;
        int outputSize = 1;

        // Weights: 2 hidden neurons * (2 inputs + 1 bias) + 1 output neuron * (2 hidden + 1 bias)
        // Hidden layer weights: [bias1, w11, w12, bias2, w21, w22]
        // Output layer weights: [bias3, w31, w32]
        double[] weights = {0.5, 0.2, 0.3, -0.1, 0.4, -0.2, 0.1, 0.6, 0.8};

        // Normalization params (min, max)
        double[][] inputNormParams = {{0, 10}, {0, 10}};
        double[][] outputNormParams = {{0, 100}};

        NeuralNetworkCalculator calculator = new NeuralNetworkCalculator(
            inputSize, hiddenSize, outputSize, weights, inputNormParams, outputNormParams);

        // Input [5, 5] normalized to [0, 0] (midpoint of [0,10] maps to 0 in [-1,1])
        double[] input = {5, 5};

        // Hidden layer computation:
        // Neuron 1: 0.5 + 0.2*0 + 0.3*0 = 0.5 -> tanh(0.5)  0.46211715726000974
        // Neuron 2: -0.1 + 0.4*0 + (-0.2)*0 = -0.1 -> tanh(-0.1)  -0.09966799462495582
        // Output layer computation:
        // Output: 0.1 + 0.6*0.46211715726000974 + 0.8*(-0.09966799462495582) = 0.2975358986560412
        // Since output normalization maps [0,100] to [-1,1], we need to denormalize 0.2975358986560412
        // 0.2975358986560412 in [-1,1] maps to ((0.2975358986560412+1)/2) * 100 = 64.87679493280206

        double[] result = calculator.calculate(input);
        assertEquals(1, result.length);
        assertEquals(64.87679493280206, result[0], 0.001);
    }

    @Test
    public void testDenormalizeOutputMethod() {
        // Setup a network with 1 input, 1 hidden, 2 outputs
        int inputSize = 1;
        int hiddenSize = 1;
        int outputSize = 2;

        // Weights for hidden and output layers
        double[] weights = {0, 1, 0, 1, 1, 1}; // Simplified weights

        // Normalization params
        double[][] inputNormParams = {{0, 10}};
        double[][] outputNormParams = {{0, 100}, {-50, 50}}; // Different ranges for outputs

        NeuralNetworkCalculator calculator = new NeuralNetworkCalculator(
            inputSize, hiddenSize, outputSize, weights, inputNormParams, outputNormParams);

        // Test denormalization directly with a mock normalized output
        // This test verifies that the denormalization logic works correctly
        double[] input = {5}; // Normalizes to 0

        // With these weights and input, after hidden and output computation,
        // we should get normalized outputs that when denormalized give specific values
        double[] result = calculator.calculate(input);

        // The exact values depend on the network computation, but they should be denormalized
        // according to their respective ranges
        assertEquals(2, result.length);
        // First output range [0,100] should produce value in that range
        assertTrue(result[0] >= 0 && result[0] <= 100);
        // Second output range [-50,50] should produce value in that range
        assertTrue(result[1] >= -50 && result[1] <= 50);
    }
}

