import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;

class RegionTextureManager {
    private Map<String, TexturePack> regionPacks = new HashMap<>();
    private TexturePack defaultPack;
    private Set<String> excludedPlayers = new HashSet<>();

    public RegionTextureManager(TexturePack defaultPack) {
        this.defaultPack = defaultPack;
    }

    public void addRegionPack(String regionId, TexturePack pack) {
        regionPacks.put(regionId, pack);
    }

    public TexturePack getTexturePack(String regionId) {
        return regionPacks.getOrDefault(regionId, defaultPack);
    }

    public void setPlayerExcluded(String playerId, boolean excluded) {
        if (excluded) {
            excludedPlayers.add(playerId);
        } else {
            excludedPlayers.remove(playerId);
        }
    }

    public boolean isExcluded(String playerId) {
        return excludedPlayers.contains(playerId);
    }

    public String handlePlayerMovement(String playerId, String fromRegionId, String toRegionId) {
        if (isExcluded(playerId)) {
            return "Player " + playerId + " is excluded from automatic texture changes";
        }

        TexturePack fromPack = fromRegionId == null ? defaultPack : regionPacks.getOrDefault(fromRegionId, defaultPack);
        TexturePack toPack = toRegionId == null ? defaultPack : regionPacks.getOrDefault(toRegionId, defaultPack);

        // Check if both packs are effectively the same
        boolean samePack = (fromPack == null && toPack == null) || 
                          (fromPack != null && toPack != null && 
                           ((fromPack == defaultPack && toPack == defaultPack) ||
                            (fromPack.isCustom() == toPack.isCustom() && fromPack.getName().equals(toPack.getName()))));

        if (samePack) {
            return "No texture change needed - same pack in both regions";
        }

        if (toRegionId == null) {
            return "Applied default texture pack to player " + playerId;
        }

        TexturePack pack = regionPacks.get(toRegionId);
        if (pack != null) {
            return "Applied texture pack " + pack.getName() + " to player " + playerId;
        } else {
            return "Applied default texture pack to player " + playerId;
        }
    }

    // Overloaded method for backward compatibility
    public String handlePlayerMovement(String playerId, String regionId) {
        return handlePlayerMovement(playerId, null, regionId);
    }
}

