        TexturePack toPack = toRegionId == null ? defaultPack : regionPacks.getOrDefault(toRegionId, defaultPack);

        // Check if entering a custom region
        if (toPack != null && toPack.isCustom()) {
            setPlayerExcluded(playerId, true);
            return "NOTE: Player " + playerId + " entered custom region and is excluded from automatic changes";
        }

        // Check if leaving a custom region
        if (fromPack != null && fromPack.isCustom()) {
            setPlayerExcluded(playerId, false);
            return "Player " + playerId + " left custom region - use command to re-enable auto changes";
        }

        // Check if player is excluded (after handling custom region transitions)
        if (isExcluded(playerId)) {
            return "Player " + playerId + " is excluded from automatic texture changes";
        }

        // Check if both packs are effectively the same
        boolean samePack = (fromPack == null && toPack == null) || 
                          (fromPack != null && toPack != null && 
                           ((fromPack == defaultPack && toPack == defaultPack) ||
                            (fromPack.isCustom() == toPack.isCustom() && fromPack.getName().equals(toPack.getName()))));

        if (samePack) {
            return "No texture change needed - same pack in both regions";
        }

        if (toRegionId == null) {
            return "Applied default texture pack to player " + playerId;
        }

        TexturePack pack = regionPacks.get(toRegionId);
        if (pack != null) {
            return "Applied texture pack " + pack.getName() + " to player " + playerId;
        } else {
            return "Applied default texture pack to player " + playerId;
        }
    }

    // Overloaded method for backward compatibility
    public String handlePlayerMovement(String playerId, String regionId) {
        return handlePlayerMovement(playerId, null, regionId);
    }
