import java.util.*;

public class EnhancedSpotLight {
    private PointLight light;
    private Vector3 direction;
    private double cutoff;
    private double outerCutoff;
    private double falloffExponent;

    public EnhancedSpotLight() {}

    public EnhancedSpotLight(PointLight light, Vector3 direction, double cutoff, double outerCutoff, double falloffExponent) {
        setLight(light);
        setDirection(direction);
        setCutoff(cutoff);
        setOuterCutoff(outerCutoff);
        setFalloffExponent(falloffExponent);
    }

    public double calculateIntensity(Vector3 point) {
        // Get the position of the light
        Vector3 lightPos = light.getPosition();

        // Calculate the direction from the light to the point
        Vector3 lightToPoint = point.sub(lightPos);
        double distance = lightToPoint.length();

        // If the point is at the same position as the light, return full intensity
        if (distance == 0) {
            return light.getIntensity();
        }

        // Normalize the direction vector
        Vector3 lightDir = lightToPoint.normalized();

        // Normalize the spotlight direction
        Vector3 spotDir = this.direction.normalized();

        // Calculate the cosine of the angle between the light direction and spotlight direction
        double cosTheta = lightDir.dot(spotDir);

        // Convert cutoff angles from degrees to radians
        double cutoffRad = Math.toRadians(cutoff);
        double outerCutoffRad = Math.toRadians(outerCutoff);

        // Calculate the cosine of the cutoff angles
        double cosCutoff = Math.cos(cutoffRad);
        double cosOuterCutoff = Math.cos(outerCutoffRad);

        // If outside the outer cutoff, return 0
        if (cosTheta < cosOuterCutoff) {
            return 0.0;
        }

        // Calculate distance attenuation
        double attenuation = 1.0 / (light.getConstant() + light.getLinear() * distance + light.getExponent() * distance * distance);

        // If inside the inner cutoff, return full light intensity with distance attenuation
        if (cosTheta >= cosCutoff) {
            return light.getIntensity() * attenuation;
        }

        // Smooth transition between inner and outer cutoff
        // Map cosTheta from [cosOuterCutoff, cosCutoff] to [0, 1]
        double intensityFactor = (cosTheta - cosOuterCutoff) / (cosCutoff - cosOuterCutoff);

        // Apply falloff exponent
        intensityFactor = Math.pow(intensityFactor, falloffExponent);

        return light.getIntensity() * intensityFactor * attenuation;
    }

    // Getters and setters
    public PointLight getLight() { return light; }
    public void setLight(PointLight light) { 
        if (light == null) {
            throw new IllegalArgumentException();
        }
        this.light = light; 
    }
    public Vector3 getDirection() { return direction; }
