import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.*;

class TestCases {
    @Test
    public void testDistanceAttenuationWithAngularAttenuation() {
        // Create a point light with attenuation coefficients
        Vector3 lightPos = new Vector3(0, 0, 0);
        PointLight pointLight = new PointLight(lightPos, 100.0, 1.0, 0.1, 0.01);

        // Create a spotlight
        Vector3 direction = new Vector3(0, 0, -1);
        EnhancedSpotLight spotLight = new EnhancedSpotLight(pointLight, direction, 30, 45, 2.0);

        // Test point directly in front of the light (within inner cone)
        Vector3 point1 = new Vector3(0, 0, -10);
        double intensity1 = spotLight.calculateIntensity(point1);

        // Calculate expected distance attenuation: 1 / (1 + 0.1*10 + 0.01*100) = 1 / (1 + 1 + 1) = 1/3
        double expectedDistanceAttenuation = 1.0 / (1.0 + 0.1 * 10 + 0.01 * 10 * 10);
        double expectedIntensity1 = 100.0 * expectedDistanceAttenuation;

        assertEquals(expectedIntensity1, intensity1, 0.001, "Intensity should include distance attenuation");

        // Test point at the edge of the outer cone (should be 0)
        Vector3 point2 = new Vector3(10, 0, -10); // 45 degrees from direction
        double intensity2 = spotLight.calculateIntensity(point2);

        assertEquals(0.0, intensity2, 0.001, "Intensity should be 0 outside outer cutoff");

        // Test point between inner and outer cone with distance
        Vector3 point3 = new Vector3(0, 0, -10);
        // Rotate point to be at 37.5 degrees (between 30 and 45)
        double angle = Math.toRadians(37.5);
        double z = -10 * Math.cos(angle);
        double x = 10 * Math.sin(angle);
        point3 = new Vector3(x, 0, z);

        double intensity3 = spotLight.calculateIntensity(point3);

        // Calculate angular factor
        Vector3 lightToPoint = point3.sub(lightPos);
        Vector3 lightDir = lightToPoint.normalized();
        Vector3 spotDir = direction.normalized();
        double cosTheta = lightDir.dot(spotDir);

        double cosCutoff = Math.cos(Math.toRadians(30));
        double cosOuterCutoff = Math.cos(Math.toRadians(45));
        double angularFactor = (cosTheta - cosOuterCutoff) / (cosCutoff - cosOuterCutoff);
        angularFactor = Math.pow(angularFactor, 2.0);

        double distance3 = lightToPoint.length();
        double distanceAttenuation3 = 1.0 / (1.0 + 0.1 * distance3 + 0.01 * distance3 * distance3);
        double expectedIntensity3 = 100.0 * angularFactor * distanceAttenuation3;

        assertEquals(expectedIntensity3, intensity3, 0.001, "Intensity should combine angular and distance attenuation");
    }

    @Test
    public void testNoAttenuationAtLightPosition() {
        Vector3 lightPos = new Vector3(5, 5, 5);
        PointLight pointLight = new PointLight(lightPos, 50.0, 1.0, 0.0, 0.0);

        Vector3 direction = new Vector3(0, 0, -1);
        EnhancedSpotLight spotLight = new EnhancedSpotLight(pointLight, direction, 30, 45, 1.0);

        // Point at the same position as the light
        Vector3 point = new Vector3(5, 5, 5);
        double intensity = spotLight.calculateIntensity(point);

        assertEquals(50.0, intensity, 0.001, "Intensity should be full when at light position");
    }
}

