import java.util.*;

public class FrequencyPlanner {
    // Inner class for FrequencyAssignment
    public static class FrequencyAssignment {
        private double bandwidth;

        public FrequencyAssignment(double bandwidth) {
            this.bandwidth = bandwidth;
        }

        public double getBandwidth() {
            return bandwidth;
        }
    }

    // Inner class for Channel
    public static class Channel {
        private double bandwidth;

        public Channel(double bandwidth) {
            this.bandwidth = bandwidth;
        }

        public double getBandwidth() {
            return bandwidth;
        }
    }

    // Inner class for Plan
    public static class Plan {
        private List<FrequencyAssignment> assignments;
        private List<Channel> channels;

        public Plan() {
            this.assignments = new ArrayList<>();
            this.channels = new ArrayList<>();
        }

        public void addAssignment(FrequencyAssignment assignment) {
            this.assignments.add(assignment);
        }

        public void addChannel(Channel channel) {
            this.channels.add(channel);
        }

        public List<FrequencyAssignment> getAssignments() {
            return new ArrayList<>(assignments);
        }

        public List<Channel> getChannels() {
            return new ArrayList<>(channels);
        }

        public double getTotalRequiredBandwidth() {
            double total = 0.0;
            for (FrequencyAssignment assignment : assignments) {
                total += assignment.getBandwidth();
            }
            return total;
        }

        public double getTotalAvailableBandwidth() {
            double total = 0.0;
            for (Channel channel : channels) {
                total += channel.getBandwidth();
            }
            return total;
        }
    }

    public static Plan createPlan(double[] requiredBandwidths, double[] availableBandwidths) {
        Plan plan = new Plan();

        // Process required bandwidths (assignments)
        if (requiredBandwidths != null) {
            for (double bandwidth : requiredBandwidths) {
                if (bandwidth > 0) {
                    plan.addAssignment(new FrequencyAssignment(bandwidth));
                }
            }
        }

        // Process available bandwidths (channels)
        if (availableBandwidths != null) {
            for (double bandwidth : availableBandwidths) {
                if (bandwidth > 0) {
                    plan.addChannel(new Channel(bandwidth));
                }
            }
        }

        return plan;
    }
}

