        assertNotNull(plan.getChannels());
        assertTrue(plan.getAssignments().isEmpty());
        assertTrue(plan.getChannels().isEmpty());
    }

    @Test
    public void testPlanAddAndGetMethods() {
        Plan plan = new Plan();
        FrequencyAssignment fa = new FrequencyAssignment(5.0);
        Channel c = new Channel(15.0);

        plan.addAssignment(fa);
        plan.addChannel(c);

        assertEquals(1, plan.getAssignments().size());
        assertEquals(1, plan.getChannels().size());
        assertEquals(5.0, plan.getAssignments().get(0).getBandwidth(), 0.001);
        assertEquals(15.0, plan.getChannels().get(0).getBandwidth(), 0.001);
    }

