        plan.addAssignment(new FrequencyAssignment(5.0));
        plan.addAssignment(new FrequencyAssignment(3.0));
        plan.addAssignment(new FrequencyAssignment(2.0));

        assertEquals(10.0, plan.getTotalRequiredBandwidth(), 0.001);

        Plan emptyPlan = new Plan();
        assertEquals(0.0, emptyPlan.getTotalRequiredBandwidth(), 0.001);
    }

    @Test
    public void testGetTotalAvailableBandwidth() {
        Plan plan = new Plan();
        plan.addChannel(new Channel(10.0));
        plan.addChannel(new Channel(7.5));
        plan.addChannel(new Channel(2.5));

        assertEquals(20.0, plan.getTotalAvailableBandwidth(), 0.001);

        Plan emptyPlan = new Plan();
        assertEquals(0.0, emptyPlan.getTotalAvailableBandwidth(), 0.001);
    }

    @Test
    public void testCreatePlanWithValidBandwidths() {
        double[] required = {5.0, 3.0, 2.0};
        double[] available = {10.0, 7.5, 2.5};

        Plan plan = createPlan(required, available);

        assertEquals(3, plan.getAssignments().size());
