import java.util.*;

class AdvancedDotProductCalculator {
    public static int calculateDotProduct(int[] array1, int[] array2) {
        if (array1 == null) {
            throw new IllegalArgumentException("First array cannot be null");
        }
        if (array2 == null) {
            throw new IllegalArgumentException("Second array cannot be null");
        }
        if (array1.length != array2.length) {
            throw new IllegalArgumentException("Arrays must have the same length");
        }

        int dotProduct = 0;
        for (int i = 0; i < array1.length; i++) {
            dotProduct += array1[i] * array2[i];
        }

        return dotProduct;
    }
}

