    public static int calculateDotProduct(int[] array1, int[] array2, int threadCount) {
        if (array1 == null) {
            throw new IllegalArgumentException("First array cannot be null");
        }
        if (array2 == null) {
            throw new IllegalArgumentException("Second array cannot be null");
        }
        if (array1.length != array2.length) {
            throw new IllegalArgumentException("Arrays must have the same length");
        }

        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        List<Future<Integer>> futures = new ArrayList<>();

        int chunkSize = array1.length / threadCount;
        int remainder = array1.length % threadCount;

        int startIndex = 0;
        for (int i = 0; i < threadCount; i++) {
            int currentChunkSize = chunkSize + (i < remainder ? 1 : 0);
            int endIndex = startIndex + currentChunkSize;

            final int start = startIndex;
            final int end = endIndex;

            Callable<Integer> task = () -> {
                int partialSum = 0;
                for (int j = start; j < end; j++) {
                    partialSum += array1[j] * array2[j];
                }
                return partialSum;
            };

            futures.add(executor.submit(task));
            startIndex = endIndex;
        }

        int totalSum = 0;
        try {
