            result.put("categoryNames", categoryNames);
            result.put("hasEmptyCategories", false);
            result.put("categoryNameLengths", new ArrayList<Integer>());

            return result;
        }

        // Check if both JSONs have success field equal to 1
        boolean categoriesSuccess = hasSuccessField(categoriesJson);
        boolean cartSuccess = hasSuccessField(cartJson);

        List<String> categoryNames;
        int totalCartItems;

        if (categoriesSuccess && cartSuccess) {
            // Parse categories JSON to extract category names
            categoryNames = parseCategoryNames(categoriesJson);

            // Parse cart JSON to extract product quantities
            totalCartItems = parseCartQuantities(cartJson);
        } else {
            // Treat as no data - zero counts and empty lists
            categoryNames = new ArrayList<>();
            totalCartItems = 0;
        }

        // Calculate results
        int categoryCount = categoryNames.size();
        boolean hasEmptyCategories = categoryNames.stream().anyMatch(name -> name.isEmpty());
        List<Integer> categoryNameLengths = categoryNames.stream()
            .map(String::length)
            .collect(Collectors.toList());

        // Build result map
        result.put("categoryCount", categoryCount);
        result.put("totalCartItems", totalCartItems);
        result.put("categoryNames", categoryNames);
        result.put("hasEmptyCategories", hasEmptyCategories);
        result.put("categoryNameLengths", categoryNameLengths);

        return result;
