    private static boolean hasSuccessField(String json) {
        // Check if JSON contains "success":1
        return json.contains("\"success\":1");
    }

    private static List<String> parseCategoryNames(String categoriesJson) {
        List<String> names = new ArrayList<>();

        // Handle null or missing 'name' fields
        String[] categoryBlocks = categoriesJson.split("\\{");
        for (String block : categoryBlocks) {
