import java.util.ArrayList;
import java.util.List;
import java.util.Map;
import java.util.HashMap;
import java.util.stream.Collectors;

class DataProcessor {

    public static Map<String, Object> processEcommerceData(String categoriesJson, String cartJson) {
        Map<String, Object> result = new HashMap<>();

        // Check for null or empty input strings
        if (categoriesJson == null || categoriesJson.trim().isEmpty() ||
            cartJson == null || cartJson.trim().isEmpty()) {
            // Treat as no data
            List<String> categoryNames = new ArrayList<>();
            int totalCartItems = 0;

            result.put("categoryCount", 0);
            result.put("totalCartItems", totalCartItems);
            result.put("categoryNames", categoryNames);
            result.put("hasEmptyCategories", true); // true when no categories
            result.put("categoryNameLengths", new ArrayList<Integer>());

            return result;
        }

        // Validate JSON format before processing
        validateJsonFormat(categoriesJson);
        validateJsonFormat(cartJson);

        // Check if both JSONs have success field equal to 1
        boolean categoriesSuccess = hasSuccessField(categoriesJson);
        boolean cartSuccess = hasSuccessField(cartJson);

        List<String> categoryNames;
        int totalCartItems;

        if (categoriesSuccess && cartSuccess) {
            // Parse categories JSON to extract category names
            categoryNames = parseCategoryNames(categoriesJson);

            // Parse cart JSON to extract product quantities
            totalCartItems = parseCartQuantities(cartJson);
        } else {
            // Treat as no data - zero counts and empty lists
            categoryNames = new ArrayList<>();
            totalCartItems = 0;
        }

        // Calculate results
        int categoryCount = categoryNames.size();
        boolean hasEmptyCategories = categoryCount == 0 || categoryNames.stream().anyMatch(name -> name.isEmpty());
        List<Integer> categoryNameLengths = categoryNames.stream()
            .map(String::length)
            .collect(Collectors.toList());

        // Build result map
        result.put("categoryCount", categoryCount);
        result.put("totalCartItems", totalCartItems);
        result.put("categoryNames", categoryNames);
        result.put("hasEmptyCategories", hasEmptyCategories);
        result.put("categoryNameLengths", categoryNameLengths);

        return result;
    }

    private static void validateJsonFormat(String json) {
        // Basic JSON format validation
        String trimmed = json.trim();

        // Check for basic JSON structure
        if (!trimmed.startsWith("{") || !trimmed.endsWith("}")) {
            throw new IllegalArgumentException("Invalid JSON format: must start with { and end with }");
        }

        // Check for unmatched braces
        int braceCount = 0;
        boolean inString = false;
        boolean escaped = false;

        for (char c : trimmed.toCharArray()) {
            if (escaped) {
                escaped = false;
                continue;
            }

            if (c == '\\') {
                escaped = true;
                continue;
            }

            if (c == '"') {
                inString = !inString;
                continue;
            }

            if (!inString) {
                if (c == '{' || c == '[') {
                    braceCount++;
                } else if (c == '}' || c == ']') {
                    braceCount--;
                    if (braceCount < 0) {
                        throw new IllegalArgumentException("Invalid JSON format: unmatched closing brace");
                    }
                }
            }
        }

        if (braceCount != 0) {
            throw new IllegalArgumentException("Invalid JSON format: unmatched braces");
        }

        if (inString) {
            throw new IllegalArgumentException("Invalid JSON format: unterminated string");
        }
    }

    private static boolean hasSuccessField(String json) {
        // Check if JSON contains "success":1
        return json.contains("\"success\":1");
    }

    private static List<String> parseCategoryNames(String categoriesJson) {
        List<String> names = new ArrayList<>();

        // Check for empty categories array
        if (categoriesJson.contains("\"categories\":[]")) {
            return names; // Return empty list for empty array
        }

        // Handle null or missing 'name' fields
        String[] categoryBlocks = categoriesJson.split("\\{");
        for (String block : categoryBlocks) {
            // Skip blocks that don't represent actual category objects
            if (!block.contains("}") || block.trim().isEmpty() || block.equals("]") || !block.contains("name")) {
                continue;
            }

            if (block.contains("name")) {
                if (block.contains("\"name\":null") || block.contains("\"name\":\"\"")) {
                    names.add(""); // null or empty name becomes empty string
                } else {
                    String[] parts = block.split("\"name\":\"");
                    if (parts.length > 1) {
                        String part = parts[1];
                        int endIndex = part.indexOf('"');
                        if (endIndex > 0) {
                            names.add(part.substring(0, endIndex));
                        } else {
                            names.add(""); // Empty category name
                        }
                    }
                }
            } else {
                // Category object without name field - treat as empty string
                names.add("");
            }
        }

        return names;
    }

    private static int parseCartQuantities(String cartJson) {
        int total = 0;

        // Handle null or missing 'quantity' fields
        String[] productBlocks = cartJson.split("\\{");
        for (String block : productBlocks) {
            if (block.contains("quantity")) {
                if (block.contains("\"quantity\":null")) {
                    total += 0; // null quantity becomes 0
                } else {
                    String[] parts = block.split("\"quantity\":");
                    if (parts.length > 1) {
                        String part = parts[1];
                        StringBuilder numberStr = new StringBuilder();
                        for (char c : part.toCharArray()) {
                            if (Character.isDigit(c)) {
                                numberStr.append(c);
                            } else if (numberStr.length() > 0) {
                                break;
                            }
                        }
                        if (numberStr.length() > 0) {
                            total += Integer.parseInt(numberStr.toString());
                        }
                    }
                }
            } else if (block.contains("}") && !block.trim().isEmpty() && !block.equals("]")) {
                // Product object without quantity field - treat as 0
                total += 0;
            }
        }

        return total;
    }
}

