                        if (newR >= 1 && newR <= boardSize && newC >= 1 && newC <= boardSize && map[newR][newC] == '*') {
                            mineCount++;
                        }
                    }
                    map[i][j] = (char)('0' + mineCount);
                }
            }
        }

        int safeCells = 0;
        for (int i = 1; i <= boardSize; i++) {
            for (int j = 1; j <= boardSize; j++) {
                if (map[i][j] != '*') {
                    safeCells++;
                }
            }
        }
        return safeCells;
    }

    // Public getter to access board state for testing
