import java.util.*;

class MinesweeperSolver {
    private char[][] map;
    private boolean[][] visited;
    private final int[] dirR = {-1, -1, -1, 0, 0, 1, 1, 1};
    private final int[] dirC = {-1, 0, 1, -1, 1, -1, 0, 1};
    private int boardSize;

    // No-argument constructor
    public MinesweeperSolver() {}

    public int solveMinesweeper(int boardSize, List<String> minePositions) {
        this.boardSize = boardSize;
        map = new char[boardSize + 2][boardSize + 2];
        visited = new boolean[boardSize + 2][boardSize + 2];

        for (int i = 1; i <= boardSize; i++) {
            for (int j = 1; j <= boardSize; j++) {
                map[i][j] = '.';
            }
        }

        for (String pos : minePositions) {
            String[] parts = pos.split(",");
            int r = Integer.parseInt(parts[0]);
            int c = Integer.parseInt(parts[1]);
            map[r][c] = '*';
        }

        // Calculate adjacent mine counts for non-mine cells
        for (int i = 1; i <= boardSize; i++) {
            for (int j = 1; j <= boardSize; j++) {
                if (map[i][j] != '*') {
                    int mineCount = 0;
                    for (int k = 0; k < 8; k++) {
                        int newR = i + dirR[k];
                        int newC = j + dirC[k];
                        if (newR >= 1 && newR <= boardSize && newC >= 1 && newC <= boardSize && map[newR][newC] == '*') {
                            mineCount++;
                        }
                    }
                    map[i][j] = (char)('0' + mineCount);
                }
            }
        }

        int clicks = 0;

        // Phase 1: Click all unvisited '0' cells (each triggers DFS)
        for (int i = 1; i <= boardSize; i++) {
            for (int j = 1; j <= boardSize; j++) {
                if (map[i][j] == '0' && !visited[i][j]) {
                    dfsReveal(i, j);
                    clicks++;
                }
            }
        }

        // Phase 2: Click each remaining unvisited safe cell individually
        for (int i = 1; i <= boardSize; i++) {
            for (int j = 1; j <= boardSize; j++) {
                if (map[i][j] != '*' && !visited[i][j]) {
                    visited[i][j] = true;
                    clicks++;
                }
            }
        }

        return clicks;
    }

    // Public getter to access board state for testing
    public char getCellValue(int row, int col) {
        return map[row][col];
    }

    // DFS method to reveal connected safe cells when a 0-cell is clicked
    public void dfsReveal(int row, int col) {
        // Check bounds and if already visited
        if (row < 1 || row > boardSize || col < 1 || col > boardSize || visited[row][col]) {
            return;
        }

        // Don't reveal mines
        if (map[row][col] == '*') {
            return;
        }

        // Mark as visited
        visited[row][col] = true;

        // If this is a 0-cell, continue DFS to all 8 neighbors
        if (map[row][col] == '0') {
            for (int k = 0; k < 8; k++) {
                int newR = row + dirR[k];
                int newC = col + dirC[k];
                dfsReveal(newR, newC);
            }
        }
        // If this is a numbered cell (1-8), stop here (don't propagate further)
    }

    // Public getter to check if a cell is visited
    public boolean isVisited(int row, int col) {
        return visited[row][col];
    }
}

