class TestMinesweeperSolver {

    @Test
    public void testOptimalClickingWithZeroCells() {
        // Test case where clicking a 0-cell reveals multiple cells via DFS
        // Board: 3x3 with mine at (3,3)
        // Expected: 1 click on (1,1) reveals connected 0s and adjacent numbered cells
        // Then individual clicks for remaining cells
        MinesweeperSolver solver = new MinesweeperSolver();
        List<String> mines = Arrays.asList("3,3");
        int clicks = solver.solveMinesweeper(3, mines);

        // Should be minimal clicks due to DFS auto-reveal
        // (1,1) is 0 and clicking it should reveal most of the board
        assertTrue(clicks < 8, "Should use fewer clicks due to DFS optimization");
    }

    @Test
    public void testTwoPhaseClickingStrategy() {
        // Test that demonstrates the two-phase strategy:
        // Phase 1: Click 0-cells (with DFS)
        // Phase 2: Click remaining individual cells
        // For 3x3 with mine at (2,2), all corners are 0-cells but not connected
        // So we need 4 clicks on corners, then 4 clicks on edges = 8 total
        MinesweeperSolver solver = new MinesweeperSolver();
        List<String> mines = Arrays.asList("2,2");
        int clicks = solver.solveMinesweeper(3, mines);

        // With mine at center, corners are 0-cells but edges are numbered
        // Each corner click reveals only itself, then edges need individual clicks
        assertEquals(8, clicks, "Should need 8 clicks: 4 for corners + 4 for edges");
    }


    @Test
    public void testMinimalClicksWithLargeConnectedRegion() {
        // Test with a larger board where one 0-cell click reveals a large region
        MinesweeperSolver solver = new MinesweeperSolver();
        List<String> mines = Arrays.asList("4,4");
        int clicks = solver.solveMinesweeper(4, mines);

        // Should be much fewer than 15 total safe cells due to DFS optimization
        assertTrue(clicks <= 8, "Should use optimal clicking strategy for large connected regions");
    }

    @Test
