        MinesweeperSolver solver = new MinesweeperSolver();
        List<String> mines = Arrays.asList("2,2");
        solver.solveMinesweeper(3, mines);

        // Check that all safe cells are visited
        for (int i = 1; i <= 3; i++) {
            for (int j = 1; j <= 3; j++) {
                if (solver.getCellValue(i, j) != '*') {
                    assertTrue(solver.isVisited(i, j), 
                        "All safe cells should be visited after optimal clicking");
                }
            }
        }
    }
