class HttpRequestSimulator {
    public Map<String, Object> simulateHttpRequest(String url, String method, Map<String, String> params, Map<String, String> headers) {
        if (url == null || url.isEmpty()) {
            throw new IllegalArgumentException();
        }
        if (!"GET".equals(method) && !"POST".equals(method)) {
            throw new IllegalArgumentException();
        }

        // Simulate processing time between 50-200ms
        try {
            long processingTime = 50 + (long)(Math.random() * 151);
            Thread.sleep(processingTime);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }

        Map<String, Object> response = new HashMap<>();

        // Check if URL contains 'error'
        if (url.contains("error")) {
            response.put("status", 500);
            response.put("body", "Simulated server error");
        } else if ("GET".equals(method)) {
            response.put("status", 200);
            response.put("body", "Sample response body");
        } else if ("POST".equals(method)) {
            response.put("status", 201);
            response.put("body", "Sample response body");
        }

