        if (headers != null && !headers.isEmpty()) {
            Map<String, String> lowerCaseHeaders = new HashMap<>();
            for (Map.Entry<String, String> entry : headers.entrySet()) {
                lowerCaseHeaders.put(entry.getKey().toLowerCase(), entry.getValue());
            }
            response.put("headers", lowerCaseHeaders);
        }

        response.put("requestTime", System.currentTimeMillis());
        return response;
    }

    public List<Map<String, Object>> processBatchRequests(List<Map<String, Object>> requests) {
        List<Map<String, Object>> responses = new ArrayList<>();

        for (Map<String, Object> request : requests) {
            String url = (String) request.get("url");
            String method = (String) request.get("method");
            @SuppressWarnings("unchecked")
            Map<String, String> params = (Map<String, String>) request.get("params");
            @SuppressWarnings("unchecked")
            Map<String, String> headers = (Map<String, String>) request.get("headers");

            Map<String, Object> response = simulateHttpRequest(url, method, params, headers);
            responses.add(response);
        }

        return responses;
    }
}

