        grid[0][0] = true;
        grid[1][1] = true;
        grid[2][2] = true;
        assertEquals(3, GameOfLifePatterns.countLiveCells(grid));

        // Set all cells to true
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[i].length; j++) {
                grid[i][j] = true;
            }
        }
        assertEquals(9, GameOfLifePatterns.countLiveCells(grid));
    }
