        GameOfLifePatterns.applyPattern(grid, "lightweight spaceship", 2, 2);

        // Check that the correct cells are set
        assertTrue(grid[2][3]);  // (0,1) + (2,2)
        assertTrue(grid[2][6]);  // (0,4) + (2,2)
        assertTrue(grid[3][2]);  // (1,0) + (2,2)
        assertTrue(grid[4][2]);  // (2,0) + (2,2)
        assertTrue(grid[5][2]);  // (3,0) + (2,2)
        assertTrue(grid[5][6]);  // (3,4) + (2,2)
        assertTrue(grid[6][2]);  // (4,0) + (2,2)
        assertTrue(grid[6][3]);  // (4,1) + (2,2)
        assertTrue(grid[6][4]);  // (4,2) + (2,2)
        assertTrue(grid[6][5]);  // (4,3) + (2,2)

        // Check that other cells are not set
        assertFalse(grid[0][0]);
        assertFalse(grid[1][1]);
        assertFalse(grid[9][9]);

        // Verify total count
        assertEquals(10, GameOfLifePatterns.countLiveCells(grid));
    }

    @Test
    public void testTumblerPattern() {
        boolean[][] grid = GameOfLifePatterns.createGrid(10);
        GameOfLifePatterns.applyPattern(grid, "tumbler", 1, 1);

        // Check that the correct cells are set
        assertTrue(grid[1][2]);  // (0,1) + (1,1)
        assertTrue(grid[1][3]);  // (0,2) + (1,1)
        assertTrue(grid[1][5]);  // (0,4) + (1,1)
        assertTrue(grid[1][6]);  // (0,5) + (1,1)
        assertTrue(grid[2][2]);  // (1,1) + (1,1)
        assertTrue(grid[2][3]);  // (1,2) + (1,1)
        assertTrue(grid[2][5]);  // (1,4) + (1,1)
        assertTrue(grid[2][6]);  // (1,5) + (1,1)
        assertTrue(grid[3][2]);  // (2,1) + (1,1)
        assertTrue(grid[3][3]);  // (2,2) + (1,1)
        assertTrue(grid[3][5]);  // (2,4) + (1,1)
        assertTrue(grid[3][6]);  // (2,5) + (1,1)
        assertTrue(grid[4][4]);  // (3,3) + (1,1)
        assertTrue(grid[4][7]);  // (3,6) + (1,1)
        assertTrue(grid[5][4]);  // (4,3) + (1,1)
        assertTrue(grid[5][7]);  // (4,6) + (1,1)
        assertTrue(grid[6][4]);  // (5,3) + (1,1)
        assertTrue(grid[6][5]);  // (5,4) + (1,1)
        assertTrue(grid[6][6]);  // (5,5) + (1,1)
        assertTrue(grid[6][7]);  // (5,6) + (1,1)

        // Check that other cells are not set
        assertFalse(grid[0][0]);
        assertFalse(grid[1][1]);
        assertFalse(grid[9][9]);

        // Verify total count
        assertEquals(20, GameOfLifePatterns.countLiveCells(grid));
    }

    @Test
    public void testPatternsWithBoundaryConditions() {
        boolean[][] grid = GameOfLifePatterns.createGrid(5);

        // Apply a pattern with offset that would go out of bounds
        GameOfLifePatterns.applyPattern(grid, "lightweight spaceship", 3, 3);

        // Only cells within bounds should be set
        assertTrue(grid[3][4]);  // (0,1) + (3,3) = (3,4) - within bounds
        assertTrue(grid[4][3]);  // (1,0) + (3,3) = (4,3) - within bounds

        // But cells that would be out of bounds should not be set
        // For example, (4,0) + (3,3) = (7,3) which is out of bounds for a 5x5 grid

        // Apply tumbler pattern near boundary
        boolean[][] grid2 = GameOfLifePatterns.createGrid(6);
        GameOfLifePatterns.applyPattern(grid2, "tumbler", 0, 0);

        // Only cells within the 6x6 grid should be set
        assertTrue(grid2[0][1]);  // (0,1) + (0,0)
        assertTrue(grid2[5][3]);  // (5,3) + (0,0)
        assertTrue(grid2[5][4]);  // (5,4) + (0,0)
        assertTrue(grid2[5][5]);  // (5,5) + (0,0)

        // But (5,6) + (0,0) = (5,6) which is out of bounds for a 6x6 grid (indices 0-5)
        // So grid2[5][6] should not be accessed (would be out of bounds)
    }
}

