        if (totalAmount.compareTo(BigDecimal.ZERO) < 0) {
            throw new IllegalArgumentException("totalAmount cannot be negative");
        }
        if (taxRate.compareTo(BigDecimal.ZERO) < 0) {
            throw new IllegalArgumentException("taxRate cannot be negative");
        }

        BigDecimal[] result = new BigDecimal[2];

        if (!isTaxIncluded) {
            // Price is the total amount
            result[0] = totalAmount;
            // Tax is totalAmount * (taxRate / 100)
            result[1] = totalAmount.multiply(taxRate.divide(BigDecimal.valueOf(100), 10, RoundingMode.HALF_UP));
        } else {
            // Price is totalAmount / (1 + taxRate/100)
            BigDecimal divisor = BigDecimal.ONE.add(taxRate.divide(BigDecimal.valueOf(100), 10, RoundingMode.HALF_UP));
            result[0] = totalAmount.divide(divisor, 10, RoundingMode.HALF_UP);
            // Tax is totalAmount - price
            result[1] = totalAmount.subtract(result[0]);
        }

        return result;
    }
}

