import java.math.BigDecimal;
import java.math.RoundingMode;

class TaxPriceCalculator {

    public static BigDecimal[] calculatePriceAndTax(BigDecimal totalAmount, BigDecimal taxRate, boolean isTaxIncluded) {
        // Input validation
        if (totalAmount == null) {
            throw new IllegalArgumentException("totalAmount cannot be null");
        }
        if (taxRate == null) {
            throw new IllegalArgumentException("taxRate cannot be null");
        }
        if (totalAmount.compareTo(BigDecimal.ZERO) < 0) {
            throw new IllegalArgumentException("totalAmount cannot be negative");
        }
        if (taxRate.compareTo(BigDecimal.ZERO) < 0) {
            throw new IllegalArgumentException("taxRate cannot be negative");
        }

        BigDecimal[] result = new BigDecimal[2];

        if (!isTaxIncluded) {
            // Price is the total amount
            result[0] = totalAmount;
            // Tax is totalAmount * (taxRate / 100)
