            BigDecimal divisor = BigDecimal.ONE.add(taxRate.divide(BigDecimal.valueOf(100), 10, RoundingMode.HALF_UP));
            result[0] = totalAmount.divide(divisor, 10, RoundingMode.HALF_UP);
            // Tax is totalAmount - price
            result[1] = totalAmount.subtract(result[0]);
